export class Product {
    // =================================================================================================================
    // Data
    private _sku: string;
    private _name: string;
    private _price: number;
    private _tierPrice: any;
    private _mainMedia: string;

    // =================================================================================================================
    // Constructor
    constructor
 (
        sku: string = '',
        name: string = '',
        price: number = 0,
        tierPrice: any = [],
        mainMedia: string = '',
    ) {
        this.setSku(sku);
        this.setName(name);
        this.setPrice(price);
        this.setTierPrice(tierPrice);
        this.setMainMedia(mainMedia);
    }


    // =================================================================================================================
    // Getters & Setters

    public getSku(): string { return this._sku; }
    public setSku(value: string) { this._sku = value; }
    public getName(): string { return this._name; }
    public setName(value: string) { this._name = value; }
    public getPrice(): number { return this._price; }
    public setPrice(value: number) { this._price = value; }
    public getTierPrice(): any { return this._tierPrice; }
    public setTierPrice(value: any) { this._tierPrice = value; }
    public getMainMedia(): string { return this._mainMedia; }
    public setMainMedia(value: string) { this._mainMedia = value; }

    // =================================================================================================================
    // Main methods

    public static fromJSON(json: any): Product {
        const newPost = new Product(json.sku, json.name, json.price, json.tier_prices, json.media_gallery_entries[0].file);
        return newPost;
    }
    // =================================================================================================================
    // Helper methods

    public hasTierPrice()
    {
        return this._tierPrice.length > 0;
    }

}
