	import Product from "./components/Product";
	import Post from './components/Post'
	import {MagentoAPIService} from "./services/MagentoAPIService";
	import * as firebase from 'firebase/app';
	import 'firebase/messaging';
	import * as localForage from "localforage";

	export default {
		name: 'App',
		components: {
			Product,
			Post
		},
		data()
		{
			return {
				firebase:
				{
					config: {
						apiKey: "AIzaSyAyGInviSJMCyZV_XHLIRa8fKAt-wsaAJA",
						authDomain: "pwademo-763.firebaseapp.com",
						databaseURL: "https://pwademo-763.firebaseio.com",
						projectId: "pwademo-763",
						storageBucket: "pwademo-763.appspot.com",
						messagingSenderId: "857384407396"
					},
					messaging: null,
					token: null,
				},
                service_worker:
                {
                	refreshing: false,
                    reloadRequested: false
                },
                lf: null,
				newWorker: null,
				drawer: '',
				items: [
					{title: 'Home', icon: 'dashboard', link: '/'},
					{title: 'About', icon: 'question_answer', link: '/about'},
					{title: 'Products', icon: 'question_answer', link: '/products'},
					{title: 'Update', icon: 'question_answer', link: '/update'},
					{title: 'Bare Update', icon: 'question_answer', link: '/update-without-skip-and-claim'},
					{title: 'Update with skip', icon: 'question_answer', link: '/update-with-skip-no-claim'},
					{title: 'Update with skip & claim', icon: 'question_answer', link: '/update-with-skip-and-claim'},
				]
			}
		},
		computed:
		{
			posts: function ()
			{
				let posts = this.$store.getters['posts/allPosts'];
				// console.log("All Posts: ", posts);
				return posts;
			},
			products: function ()
			{
				let products = this.$store.getters['products/allProducts'];
				// console.log("All Posts: ", posts);
				return products;
			}
		},
		created()
		{
			localForage.config({
				                   name        : 'Alessio',
				                   version     : 1.0,
				                   size        : 4980736, // Size of database, in bytes. WebSQL-only for now.
				                   storeName   : 'lf', // Should be alphanumeric, with underscores.
				                   description : 'some description'
			                   });
			this.lf = localForage.createInstance({name: "lf"});
			this.sw();
			this.firebaseNotificationsHandler();

		},
		methods:
		{
			_firebaseInit()
			{
				firebase.initializeApp(this.firebase.config);
				this.firebase.messaging = firebase.messaging();
				this.firebase.messaging.usePublicVapidKey("BHEX_KV05JO3refzgJbNXaXObnSiCb2kjx1rOsLWhMon4dea2ddfiYD0iLBHm6DsQkVp8sCLr2Ypuuz6haDnOzo");
			},
			_firebaseGetToken()
			{
				const THIS = this;
				THIS.firebase.messaging
				    .getToken()
				    .then(function (currentToken)
				          {
					          if (currentToken)
					          {
						          THIS.firebase.token = currentToken;
						          console.log("TOKEN: ", currentToken);
					          } else
					          {
						          console.log('No Instance ID token available. Request permission to generate one.');
					          }
				          })
				    .catch(function (err)
				           {
					           console.log('An error occurred while retrieving token. ', err);
					           // setTokenSentToServer(false);
				           });

			},
			_firebaseRequestPermission()
			{
				this.firebase.messaging
				    .requestPermission()
				    .then(() =>
				          {
					          console.log('Notification permission granted.');
					          this._firebaseGetToken()

				          })
				    .catch((err) =>
				           {
					           console.log('Unable to get permission to notify.', err);
				           });
			},
			firebaseNotificationsHandler()
			{
				const THIS = this;
				this._firebaseInit();
				this._firebaseRequestPermission();

				// -----------------------------------------------------------------------------------------------------
				// Event listeners

				THIS.firebase.messaging
				    .onTokenRefresh(
				    () =>
				    {
					    this._firebaseGetToken();
					    console.log("TOKEN REFRESHED: ", THIS.firebase.token)
				    });

				THIS.firebase.messaging
				    .onMessage(
				    (payload) =>
				    {
					    console.log('Message received. ', payload);
					    if (Notification.permission === 'granted')
					    {
						    const notification = new Notification(payload.notification.title, {
							    icon: payload.notification.icon,
							    body: payload.notification.body,
						    });
						    notification.onclick = function ()
						    {
							    console.log("NOTIFCATION CLICK");
							    window.open(payload.notification.click_action,
							                "_blank");
						    };
						    return notification;
					    }
				    });
			},
			send_message_to_sw(msg)
			{
				if ('serviceWorker' in navigator)
				{
					navigator
					.serviceWorker.ready
					.then(function (serviceWorkerRegistration)
					      {
						      // Let's see if you have a subscription already
						      return serviceWorkerRegistration.pushManager.getSubscription();
					      })
					.then(function (subscription)
					      {
						      if (!subscription)
						      {
							      // You do not have subscription
						      }
						      // You have subscription.
						      // Send data to service worker
						      console.log("Sending to SW...");
						      navigator.serviceWorker.controller.postMessage(
						      {
							      "data":
							      {
								      "dev_comm": true,
								      "id": 1
							      }
						      });

					      })
				}
			},
			request_from_sw()
            {
	            let request = new Request("https://localhost:8080/sw.js");
	            const requestOptions = { method: 'GET'};
	            return fetch(request, requestOptions)
	            .then((response) =>
	                  {
		                  return {
			                  request: request,
			                  response: response
		                  };
	                  })
	            .catch(error =>
	                   {
		                   console.error(error);
		                   console.error(JSON.stringify(error));
		                   return {
			                   request: request,
			                   response: response
		                   };
	                   });
            },
			send_new_sw()
			{
				if(this.newWorker)
    				this.newWorker.postMessage({action: 'skipWaiting'});
			},
			sw()
			{
				window.addEventListener(
				'load',
				async () =>
				{
					navigator
					.serviceWorker
					.register('/sw.js', {scope: '/'})
					.then(
					(registration) =>
					{
						// Registration was successful
						console.log('SW GLOBAL OK: ', registration.scope);

					}, (err) =>
					{
						// registration failed :(
						console.log('SW registration failed: ', err);
					})
					.then(() =>
					      {
					      	console.log("SW GLOBAL READY");
						      return navigator.serviceWorker.ready;
					      });

					// Source: https://stackoverflow.com/questions/37573482/to-check-if-serviceworker-is-in-waiting-state
					const registration = await navigator.serviceWorker.register('/sw.js');
					if(registration.waiting && registration.active)
                    {
                        this.newWorker = registration.waiting;
                    }
					else
                    {
	                    registration.addEventListener(
	                    'updatefound',
	                    () =>
	                    {
		                    this.newWorker = registration.installing;

		                    this.newWorker
		                        .addEventListener(
		                        'statechange',
		                        () =>
		                        {
			                        // Has service worker state changed?
			                        switch (this.newWorker.state)
			                        {
				                        case 'installed':

					                        // There is a new service worker available, show the
					                        // notification
					                        if (navigator.serviceWorker.controller)
					                        {
						                        console.log("NEW SW AVAILABLE");
					                        }
					                        break;
			                        }
		                        });
	                    });
                    }
					// this.lf.setItem("newWorker", this.newWorker);



					const THIS = this;
					navigator.serviceWorker.addEventListener('controllerchange', function ()
					{
						console.log("SW CONTROLLERCHANGE ");
						if (!THIS.service_worker.refreshing && THIS.service_worker.reloadRequested)
                        {
	                        window.location.reload();
	                        THIS.service_worker.refreshing = true;

                        }
						else
                        {
	                        THIS.service_worker.refreshing = false;
	                        THIS.service_worker.reloadRequested = false;
	                        return
                        }

					});
				});

			}
		}
	}
